import React, { Component } from 'react'
import { Button, Checkbox, Form, Container, Segment, Header } from 'semantic-ui-react'


export default class Test extends Component {

    state = {
        
        size: 1,
    };

  
    addOption= (e) =>{

        //alert(e.target.id);

        if(e.target.name == this.state.size){
            alert("ADD ONE");
        }
        this.setState({
            size: this.state.size + 1,
        });

        alert(this.state.size);
    }

    onTextChange = (text, key) => {
        alert("TEXT: " + text);
        alert("KEY: " + key);
        this.setState({
          event: {
            [key]: text
          }
        })
      }


    render() {

        return (
            <div>
                <Header >MEMES!</Header>
    <Container>
        <Segment>
            <Form>
                <Form.Field>
               
                <input placeholder='Your Question' />
                </Form.Field>
                <Form.Field>
          
                <input placeholder='Poll Option #1' onChange={this.onTextChange}  onClick={ this.addOption}  name='1' />
                </Form.Field>
                <Form.Field>
                <Checkbox  label='Public' />
                </Form.Field>
                <Button type='submit'>Submit</Button>
            </Form>
        </Segment>
    </Container>
            </div>
        )
    }
}
