import React, { Component } from 'react'
import { Grid, Segment, Header, Container, Pagination } from 'semantic-ui-react'
import {Link} from 'react-router-dom'
import {getPolls} from '../../actions/pollActions';
import { connect } from 'react-redux';


class Polls extends Component {

    componentWillMount(){
        this.props.getPolls();
    }
    render() {
        const polls = [
            {text:'Should I play Shaman?',
            options: 5
            },
            {text:'Should I play Rogue?',
            options: 2},
            {text:'Should I play Warrior?',
            options: 10},
            {text:'Should I play Priest?',
            options: 32},

        ]

        return (
           <div> 
               <Header as='h3'>Last 10 Public Polls</Header>
                {this.props.polls.polls && this.props.polls.polls.map(poll=>
                <React.Fragment >
                       <a href='#' >
                    <Link to = '/poll/12312'>
                    <Segment raised  color='orange' inverted  size='huge'  style={{marginTop:'15px'}} id = {poll.id}>
               
                        {poll.title}
                        ID: {poll.id}
                    </Segment>
                    </Link>
                     </a>
                    </React.Fragment>
                 
                

                 )}
        
            
          
        </div>
        )
    }
}

const mapStateToProps = state => ({

    polls: state.polls,

  });
  
  export default connect(mapStateToProps, {getPolls})(Polls);
