import React, { Component } from 'react'
import { Icon, Label, Menu, Table, Container } from 'semantic-ui-react'
import _ from 'lodash'
import {getPolls} from '../../actions/pollActions';
import { connect } from 'react-redux';



class PollsArchive extends Component {
    state = {
        column: null,
        data: [],
        direction: null,
        itemsPerPage: 10,
        currentPage: 0,

      }
      handleSort = clickedColumn => () => {
        const { column, data, direction } = this.state
    
        if (column !== clickedColumn) {
          this.setState({
            column: clickedColumn,
            data: _.sortBy(data, [clickedColumn]),
            //direction: 'ascending',
          })
    
          return
        }
    
        this.setState({
          data: data.reverse(),
          direction: direction === 'ascending' ? 'descending' : 'ascending',
        })
      }

      componentWillMount(){

        this.props.getPolls();


      }
      componentWillReceiveProps(nextProps){
        
        const tableData = [];

        if(nextProps.polls)
          {
            
            for(const index in nextProps.polls){
              const name = nextProps.polls[index].title;
              const age = nextProps.polls[index].views;
              const gender = nextProps.polls[index].date.substring(0,10);
              tableData.push({name:name,age:age,gender:gender})
            }

          }
          this.setState({data:tableData});

      }
      //pagination methods
      handlePageClick = (page) =>{

      }
      handleNextclick = () =>{

      }

    render() {
       
           const { column, data, direction } = this.state

        return (
            <Container>
 <Table sortable celled fixed>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell
              sorted={column === 'name' ? direction : null}
              onClick={this.handleSort('name')}
              width={8}
            >
              Question
            </Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'age' ? direction : null}
              onClick={this.handleSort('age')}
              width={4}

            >
            Viewers
            </Table.HeaderCell>
            <Table.HeaderCell
              sorted={column === 'gender' ? direction : null}
              onClick={this.handleSort('gender')}
              width={2}

            >
              Date
            </Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {_.map(data, ({ age, gender, name }) => (
            <Table.Row key={name}>
              <Table.Cell>{name}</Table.Cell>
              <Table.Cell>{age}</Table.Cell>
              <Table.Cell>{gender}</Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
     

    <Table.Footer>
      <Table.Row>
        <Table.HeaderCell colSpan='3'>
          <Menu floated='right' pagination>
            <Menu.Item as='a' icon>
              <Icon name='chevron left' />
            </Menu.Item>
            <Menu.Item as='a'>1</Menu.Item>
            <Menu.Item as='a'>2</Menu.Item>
            <Menu.Item as='a'>3</Menu.Item>
            <Menu.Item as='a'>4</Menu.Item>
            <Menu.Item as='a' icon>
              <Icon name='chevron right' />
            </Menu.Item>
          </Menu>
        </Table.HeaderCell>
      </Table.Row>
    </Table.Footer>
  </Table> 
            </Container>
        )
    }
}

const mapStateToProps = state => ({

  polls: state.polls.polls,

});

export default connect(mapStateToProps, {getPolls})(PollsArchive);
