import React, { Component } from 'react'
import { Grid, Segment, Header, Container } from 'semantic-ui-react'


export default class Polls extends Component {

    render() {
        const polls = [
            {text:'Should I play Shaman?',
            options: 5
            },
            {text:'Should I play Rogue?',
            options: 2},
            {text:'Should I play Warrior?',
            options: 10},
            {text:'Should I play Priest?',
            options: 32},

        ]

        return (
            <div>
                {polls.map(poll=>
                    
                  <div>
                  
    <Segment basic>Left</Segment>
    <Segment basic>Middle</Segment>
    <Segment basic>Right</Segment>

                </div>
                

                 )}
            </div>
            
      
        )
    }
}
