import axios from 'axios';
import {GET_POLLS, GET_ERRORS} from './types';



export const getPolls = ()=>dispatch=>{
    axios
    .get('http://localhost:5000/api/polls/')
    .then(res => 
        dispatch({
        type:GET_POLLS,
        payload: res.data,
        }))
    .catch(err=>
        dispatch({
            type: GET_ERRORS,
            payload: 'No Polls',
        })    
    )

}
