import axios from 'axios';
import {GET_POLLS, GET_POLL, GET_POLL_TEST, GET_ERRORS} from './types';

import { browserHistory } from 'react-router';


//defining base URL
axios.defaults.baseURL = 'http://localhost:5000/';


export const getPolls = ()=>dispatch=>{
    axios
    .get('/api/polls/')
    .then(res => 
        dispatch({
        type:GET_POLLS,
        payload: res.data,
        }))
    .catch(err=>
        dispatch({
            type: GET_ERRORS,
            payload: 'No polls available.',
        })    
    )

}
export const getPoll = (id)=>dispatch=>{
    axios
    .get(`api/polls/${id}`)
    .then(res => 
        dispatch({
        type:GET_POLL,
        payload: res.data,
        }))
    .catch(err=>
        dispatch({
            type: GET_ERRORS,
            payload: 'No poll with that id.',
        })    
    )

}

export const getPollTest = (id,history)=>dispatch=>{
    history.push("/");

}
