import React, { Component } from 'react'
import { Button, Checkbox, Form, Container, Segment, Header } from 'semantic-ui-react'


export default class Test extends Component {

    state = {
        options:[{text: 'option 1', id:1}],
        size: 1,

    };

  
    addOption= (e) =>{

        if(e.target.id == this.state.size){

            const option = {
                text:'memes #1',
                id: this.state.size + 1
            };  
            this.setState({ options: [...this.state.options, option ], size: this.state.size+1 }) 
        }
    
    }

    onTextChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
          })
      

    }


    render() {
        const {options, size} = this.state;

        return (
            <div>
                <Header >MEMES!</Header>
    <Container>
        <Segment>
            <Form>
                <Form.Field>
               
                <input placeholder='Your Question' />
                </Form.Field>
                <Form.Field>
                {options.map(option => 
                    <input placeholder='Poll Option #1'  onChange={this.onTextChange}  onClick={ this.addOption}  name='1' />
                )}
                </Form.Field>
                <Form.Field>
                <Checkbox  label='Public' />
                </Form.Field>
                <Button type='submit'>Submit</Button>
            </Form>
        </Segment>
    </Container>
            </div>
        )
    }
}
