import React, { Component } from 'react'
import {Segment, Header } from 'semantic-ui-react'
import { connect } from 'react-redux';
import {getPoll} from '../../actions/pollActions';

import {HorizontalBar} from 'react-chartjs-2'

import Spinner from '../Spinner'


 
class PollDetail extends Component {
  state = {
    loading:true,
    data:[]
  }
    componentWillMount(){
      this.props.getPoll(this.props.id);
    }
    componentWillReceiveProps(nextProps){
     
      const chartLabels = [];
      const chartData = [];
      for(const index in nextProps.poll.options){
      
        chartLabels.push(nextProps.poll.options[index].text);
        chartData.push(nextProps.poll.options[index].voters.length);
      }
     
      const data={
        labels: chartLabels,
        datasets: [
          {
            label: 'What Should I Roll?',
            backgroundColor: 'orange',
            borderColor: 'rgba(255,99,132,1)',
            borderWidth: 1,
            hoverBackgroundColor: 'yellow',
            hoverBorderColor: 'yellow',
            data: chartData
          }
        ],
        options:{
          responsive: true,
          maintainAspectRatio: true,
        }
      
      };
      this.setState({data:data})
      
    }

    render() {
        return (
            <div>         
    <Header textAlign='center'>Your Question</Header>
    <HorizontalBar data={this.state.data}  height={80}  width={1}/>
    <Segment.Group horizontal >
      <Segment>Voters: 500</Segment>
      <Segment>Published: 12341</Segment>
      <Segment>Bottom</Segment>
    </Segment.Group>
    <Segment>Sharable Link: www.memes.com/123133</Segment>
           
            </div>
        )
    }
}

const mapStateToProps = state => ({
  poll: state.polls.poll
});


export default connect(mapStateToProps, {getPoll})(PollDetail);
