import React, { Component } from 'react'
import { Grid, Segment, Header, Container, Card } from 'semantic-ui-react'


export default class Polls extends Component {

    render() {
        const polls = [
            {text:'Should I play Shaman?',
            options: 5
            },
            {text:'Should I play Rogue?',
            options: 2},
            {text:'Should I play Warrior?',
            options: 10},
            {text:'Should I play Priest?',
            options: 32},

        ]

        return (
            <div>
                {polls.map(poll=>
                    
                  
                    <Card.Group>
                    <Card color='red' style={{backgroundColor:'red'}}>
                      <Card.Content>
                        <Card.Header color='red'>{poll.text}</Card.Header>
                        <Card.Meta>created 2 hours ago.</Card.Meta>
                        <Card.Description>{poll.options} voters</Card.Description>
                      </Card.Content>
                    </Card>
                    </Card.Group>
              
          
                

                 )}
            </div>
            
      
        )
    }
}
