import { GET_POLLS, GET_POLL  } from '../actions/types';

const initialState = {
    loading: false,
    
};

export default function(state = initialState, action) {

  switch (action.type) {
    case GET_POLLS:
      return {
        ...state,
        polls:action.payload
    
      }
      case GET_POLL:
      return {
        ...state,
        poll:action.payload
    
      }
    
    default:
      return state;
  }
}
