import React, { Component } from 'react'
import { Icon, Label, Menu, Table, Container } from 'semantic-ui-react'
import _ from 'lodash'
import {getPolls} from '../../actions/pollActions';
import { connect } from 'react-redux';



class PollsArchive extends Component {
    state = {
        column: null,
        data: [],
        direction: null,
        itemsPerPage: 10,
        currentPage: 0,
        totalItems: 50,
        activeItem: 0,
      }
      
      componentWillMount(){

        this.props.getPolls();


      }
      componentWillReceiveProps(nextProps){
        
        const tableData = [];

        if(nextProps.polls)
          {
            
            for(const index in nextProps.polls){
              const title = nextProps.polls[index].title;
              const views = nextProps.polls[index].views;
              const date = nextProps.polls[index].date.substring(0,10);
              tableData.push({title:title,views:views,date:date})
            }

          }
          this.setState({data:tableData});

      }
      //pagination methods
      handlePageClick = (page) =>{

      }
      handleNextclick = () =>{

      }
      handleItemClick = (e, { name }) => {
        this.setState({ activeItem: name});
        
    }

    render() {
       const {data, activeItem} = this.state; 

        return (
            <Container>
 <Table celled>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell>Question</Table.HeaderCell>
        <Table.HeaderCell>Viewers</Table.HeaderCell>
        <Table.HeaderCell>Date</Table.HeaderCell>
      </Table.Row>
    </Table.Header>

    <Table.Body>
    {
        data.splice(this.state.activeItem*this.state.itemsPerPage,(this.state.activeItem*this.state.itemsPerPage) + this.state.itemsPerPage).map(question =>
          <React.Fragment>
          <Table.Row>
          <Table.Cell>
            SPLICING FROM {this.state.activeItem*this.state.itemsPerPage
              /*question.title*/}    TO {(this.state.activeItem*this.state.itemsPerPage) + this.state.itemsPerPage}
         </Table.Cell>
         <Table.Cell>{question.views}</Table.Cell>
         <Table.Cell>{question.date.substring(0,10)}</Table.Cell>
       </Table.Row>
      
         </React.Fragment>
        )
      
      }
     
          
 
    </Table.Body>

    <Table.Footer>
      <Table.Row>
        <Table.HeaderCell colSpan='3'>
          <Menu floated='right' pagination>
            <Menu.Item as='a' icon>
              <Icon name='chevron left' />
            </Menu.Item>
            {[...Array(this.state.totalItems/this.state.itemsPerPage)].map((x, index) =>
            <Menu.Item  name={index} active={activeItem === index} onClick={this.handleItemClick}>{index+1}</Menu.Item>
            )}

    
            <Menu.Item as='a' icon>
              <Icon name='chevron right' />
            </Menu.Item>
          </Menu>
        </Table.HeaderCell>
      </Table.Row>
    </Table.Footer>
  </Table>
            </Container>
        )
    }
}

const mapStateToProps = state => ({

  polls: state.polls.polls,

});

export default connect(mapStateToProps, {getPolls})(PollsArchive);
