import React, { Component } from 'react'
import { Icon, Label, Menu, Table, Container } from 'semantic-ui-react'
import _ from 'lodash'
import {getPolls} from '../../actions/pollActions';
import { connect } from 'react-redux';



class PollsArchive extends Component {
    state = {
        column: null,
        data: [],
        direction: null,
        itemsPerPage: 10,
        currentPage: 0,

      }
     
      componentWillMount(){

        this.props.getPolls();


      }
      componentWillReceiveProps(nextProps){
        
        const tableData = [];

        if(nextProps.polls)
          {
            
            for(const index in nextProps.polls){
              const title = nextProps.polls[index].title;
              const views = nextProps.polls[index].views;
              const date = nextProps.polls[index].date.substring(0,10);
              tableData.push({title:title,views:views,date:date})
            }

          }
          this.setState({data:tableData});

      }
      //pagination methods
      handlePageClick = (page) =>{

      }
      handleNextclick = () =>{

      }
      handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
       const {data, activeItem} = this.state; 

        return (
            <Container>
 <Table celled>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell>Question</Table.HeaderCell>
        <Table.HeaderCell>Viewers</Table.HeaderCell>
        <Table.HeaderCell>Date</Table.HeaderCell>
      </Table.Row>
    </Table.Header>

    <Table.Body>
    {
        data.map(question =>
          <React.Fragment>
          <Table.Row>
          <Table.Cell>
            {question.title}
         </Table.Cell>
         <Table.Cell>{question.views}</Table.Cell>
         <Table.Cell>{question.date.substring(0,10)}</Table.Cell>
       </Table.Row>
      
         </React.Fragment>
        )
      
      }
     
          
 
    </Table.Body>

    <Table.Footer>
      <Table.Row>
        <Table.HeaderCell colSpan='3'>
          <Menu floated='right' pagination>
            <Menu.Item as='a' icon>
              <Icon name='chevron left' />
            </Menu.Item>
            <Menu.Item  name='1' active={activeItem === '1'} onClick={this.handleItemClick}>1</Menu.Item>
            <Menu.Item  name='2' active={activeItem === '2'} onClick={this.handleItemClick}>2</Menu.Item>
            <Menu.Item  name='3' active={activeItem === '3'} onClick={this.handleItemClick}>3</Menu.Item>
            <Menu.Item  name='4' active={activeItem === '4'} onClick={this.handleItemClick}>4</Menu.Item>
            <Menu.Item as='a' icon>
              <Icon name='chevron right' />
            </Menu.Item>
          </Menu>
        </Table.HeaderCell>
      </Table.Row>
    </Table.Footer>
  </Table>
            </Container>
        )
    }
}

const mapStateToProps = state => ({

  polls: state.polls.polls,

});

export default connect(mapStateToProps, {getPolls})(PollsArchive);
