import { GET_POLLS  } from '../actions/types';

const initialState = {
    loading: false
};

export default function(state = initialState, action) {

  switch (action.type) {
    case GET_POLLS:
      return {
        ...state,
        polls:action.payload
        //address: action.payload,
       
      }
    
    default:
      return state;
  }
}
