import React, { Component } from 'react'
import { Button, Checkbox, Form, Container, Segment, Header } from 'semantic-ui-react'


export default class Test extends Component {

    state = {
        memes:'',
        test:'masdad',
        size: 1,
    };

    
    addOption= (e) =>{

        //alert(e.target.id);

        if(e.target.id === this.state.size){
            alert("ADD ONE");
        }
        this.setState({
            size: e.target.id,
        });

        alert(this.state.size);
    }

    render() {



        return (
            <div>
                <Header >MEMES!</Header>
    <Container>
        <Segment>
            <Form>
                <Form.Field>
               
                <input placeholder='Your Question' />
                </Form.Field>
                <Form.Field>
          
                <input placeholder='Poll Option #1' onClick={ this.addOption}  id ='memes'/>
                </Form.Field>
                <Form.Field>
                <Checkbox  label='Public' />
                </Form.Field>
                <Button type='submit'>Submit</Button>
            </Form>
        </Segment>
    </Container>
            </div>
        )
    }
}
