import React, { Component } from 'react'
import { Grid, Segment, Header, Container, Pagination } from 'semantic-ui-react'
import {Link} from 'react-router-dom'


export default class Polls extends Component {

    render() {
        const polls = [
            {text:'Should I play Shaman?',
            options: 5
            },
            {text:'Should I play Rogue?',
            options: 2},
            {text:'Should I play Warrior?',
            options: 10},
            {text:'Should I play Priest?',
            options: 32},

        ]

        return (
           <div> 
               <Header as='h3'>Last 10 Public Polls</Header>
                {polls.map(poll=>
                <React.Fragment >
                       <a href='#' >
                    <Link to = '/poll/12312'>
                    <Segment raised  color='orange' inverted  size='huge' onClick = {()=>alert('memes')} style={{marginTop:'15px'}} >
               
                        {poll.text}
                    </Segment>
                    </Link>
                     </a>
                    </React.Fragment>
                 
                

                 )}
        
            
          
        </div>
        )
    }
}
