import React, { Component } from 'react'
import { Grid, Segment, Header, Container, Pagination } from 'semantic-ui-react'


export default class Polls extends Component {

    render() {
        const polls = [
            {text:'Should I play Shaman?',
            options: 5
            },
            {text:'Should I play Rogue?',
            options: 2},
            {text:'Should I play Warrior?',
            options: 10},
            {text:'Should I play Priest?',
            options: 32},

        ]

        return (
           <div> 
            <div style={{height:'90vh'}}>
                {polls.map(poll=>
                    <div>
                    <Segment raised  color='orange' inverted  size='huge'>
                        {poll.text}
                    </Segment>
                    <Segment raised  color='orange' inverted  size='huge'>
                        {poll.text}
                    </Segment>
                    <Segment raised  color='orange' inverted  size='huge'>
                        {poll.text}
                    </Segment>
                    <Segment raised  color='orange' inverted  size='huge'>
                        {poll.text}
                    </Segment>
                    
            
                    </div>
                

                 )}
            </div>
            
            <Pagination  defaultActivePage={5} totalPages={10} />
        </div>
        )
    }
}
